import React, { useState } from 'react';
import { CopyToClipboard } from 'react-copy-to-clipboard'

function Shade({shadeColor}) {
    const [copied,setCopied] = useState(false);
    return (
        <CopyToClipboard text={shadeColor}>
        <div className="shade" style={{backgroundColor:shadeColor}} >{shadeColor}</div>
        </CopyToClipboard>
    );
}

export default Shade;
