import React, { useState } from 'react';
import Shades from './components/Shades'
import ShadesInput from './components/ShadesInput'


function App() {
  const [color,setColor] = useState("#000");
  const onInputChange = (value)=>{
    setColor(value)
  }
  return (
          <div className="App">
          <ShadesInput color={color} />
          <Shades />
          </div>
  );
}

export default App;
